import AddEmployee from "./AddEmployee";
import EmpList from "./EmpList";

const Employee = () => {

    return (
        <div className="container">
            <h1>Employee Component</h1>
            <div className="border col-4 mx-3 my-3 px-2 py-3">
                {/* <p className="lead">Create these functionalties: </p>
                <p>View All Employees</p>
                <p>Find Employee by Name</p>
                <p>Add New Employee</p>
                <p>Update Employee</p>
                <p>Delete Employee</p>
                <p>(Modify springboot code, if needed)</p> */}
                
                        <div className="  collapse navbar-collapse" id="navbarSupportedContent">
                            <ul className="  navbar-nav me-auto mb-2 mb-lg-0">
                                <li className="  nav-item">
                                    <Link className="  nav-link" to={'/addEmp'}>Add Employee</Link>
                                </li>
                                <li className="  nav-item">
                                    <Link className="  nav-link" to={'/parent'}>Parent</Link>
                                </li>
                                <li className="  nav-item">
                                    <Link className="  nav-link" to={'/profile'}>Profile</Link>
                                </li>
                                <li className="  nav-item">
                                    <Link className="  nav-link" to={'/logout'}>Logout</Link>
                                </li>
                            </ul>
                            <form className="  d-flex" role="search">
                                <input className="  form-control me-2" type="search" placeholder="Search" />
                                <button className="  btn btn-outline-success" type="submit">Search</button>
                            </form>
                        </div>
                    </div>
                
            

            <AddEmployee />
            <EmpList />
        </div>
    );
};

export default Employee;


