import React from "react";

const UpdateEmployee = () => {
  return (
    <>
      <div>UpdateEmployee</div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="firstName">First Name:</label>
        <input
          type="text"
          id="firstName"
          name="firstName"
          value={empData.firstName}
          onChange={handleChange}
          placeholder="Enter first name"
          required
          autoFocus
        />
        {errors.firstName && <span className="error">{errors.firstName}</span>}
        <br />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={empData.email}
          onChange={handleChange}
          placeholder="Enter email"
        />
        {errors.email && <span className="error">{errors.email}</span>}
        <br />
        {/* <label htmlFor="aadhaar">Aadhaar:</label>
                <input type="number" id="aadhaar" name="aadhaar" value={empData.aadhaar} onChange={handleChange} placeholder="Enter aadhaar" />
                {errors.aadhaar && <span className="error">{errors.aadhaar}</span>}
                <br /> */}
        <label htmlFor="salary">Salary:</label>
        <input
          type="number"
          id="salary"
          name="salary"
          value={empData.salary}
          onChange={handleChange}
          placeholder="Enter salary"
        />
        {errors.salary && <span className="error">{errors.salary}</span>}
        <br />
        <input type="submit" value="Add Employee" />
      </form>
    </>
  );
};

export default UpdateEmployee;
