import React, { useEffect, useState } from 'react'
import {useDispatch} from 'react-redux'
import { setEmpObj } from '../../redux/EmpSlice';

const UpdateEmployee = () => {

 

  return (
    <>
    <div>UpdateEmployee</div>
    <form >
        <label htmlFor="firstName">First Name:</label>
        <input type="text" id="firstName" name="firstName"   placeholder="Enter first name" required autoFocus />
        {/* {errors.firstName && <span className="error">{errors.firstName}</span>} */}
        <br />
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" name="email"   placeholder="Enter email" />
        {/* {errors.email && <span className="error">{errors.email}</span>} */}
        <br />
        <label htmlFor="salary">Salary:</label>
        <input type="number" id="salary" name="salary"  placeholder="Enter salary" />
        {/* {errors.salary && <span className="error">{errors.salary}</span>} */}
        <br />
        <input type="submit" value="Update Employee"  />
    </form>
    </>
    
  )
}

export default UpdateEmployee
