import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import axios from 'axios';

const UpdateEmployee = () => {

  const backendUrl = 'http://localhost:8081/emp/update-emp/';
  const [selectedEmp,setSelectedEmp]=useState({})

const dataFromStore = useSelector(e=>e.emp.empObj)

useEffect(()=>{

  setSelectedEmp(dataFromStore)
},[dataFromStore])

const handleChange = (e)=>{
  setSelectedEmp({...selectedEmp,[e.target.name]:e.target.value})
  console.log(selectedEmp)
}

const validateForm = () => {
  let isValid = true;
  const newErrors = {};

  if (!selectedEmp.firstName.trim()) {
      newErrors.firstName = "First name is required";
      isValid = false;
  }

  if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(selectedEmp.email)) {
      newErrors.email = "Invalid email address";
      isValid = false;
  }

  if (selectedEmp.salary <= 0 || isNaN(selectedEmp.salary)) {
      newErrors.salary = "Salary must be a positive number";
      isValid = false;
  }

  setErrors(newErrors);
  return isValid;
};

const handleSubmit=(evt)=> {
  evt.preventDefault();
        if (validateForm()) {
            axios.put(backendUrl+selectedEmp._id, selectedEmp,)
                .then((resp) => {
                    alert(`${resp.data.firstName} updated successfully!`);
                    // setEmpData({ firstName: '', email: '', salary: '' });
                })
                .catch(error => {
                    console.error("Error adding employee:", error);
                });
        }
}

console.log(selectedEmp)

  return (
    <>
    <div>UpdateEmployee</div>
    <form onSubmit={handleSubmit}>
        <label htmlFor="firstName">First Name:</label>
        <input type="text" id="firstName" name="firstName" value={selectedEmp.firstName}   placeholder="Enter first name" required autoFocus onChange={handleChange} />
        {/* {errors.firstName && <span className="error">{errors.firstName}</span>} */}
        <br />
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" name="email" value={selectedEmp.email}   placeholder="Enter email" />
        {/* {errors.email && <span className="error">{errors.email}</span>} */}
        <br />
        <label htmlFor="salary">Salary:</label>
        <input type="number" id="salary" name="salary" value={selectedEmp.salary} placeholder="Enter salary" />
        {/* {errors.salary && <span className="error">{errors.salary}</span>} */}
        <br />
        <input type="submit" value="Update Employee"  />
    </form>
    </>
    
  )
}

export default UpdateEmployee
